def combine_dictionaries3(base_dict, new_dict):
    combined_dict = {}
    for key in base_dict.keys():
        last_similar = ""
        multiple_new_values = []
        combined_values = base_dict[key]
        new_values = new_dict[key]
        for new_value in new_values:
            if multiple_new_values:
                if new_value not in combined_values:
                    multiple_new_values.append(new_value)
                else:
                    if last_similar:
                        index_of_last_similar = combined_values.index(last_similar)
                        combined_values[index_of_last_similar +1:index_of_last_similar +1] = multiple_new_values
                        multiple_new_values = []
                        last_similar = new_value
                    else:
                        combined_values[0:0] = multiple_new_values
                        last_similar = new_value
                        multiple_new_values = []
            else:
                if new_value not in combined_values:
                    multiple_new_values.append(new_value)
                else:
                    last_similar = new_value
        combined_dict[key] = combined_values
    if multiple_new_values:
        combined_values += multiple_new_values        

    return combined_dict
